:root {
  --grid-row-size: 48px;
  --row-gap: 16px;
  --columns: 12;
  --column-gap: 24px;
  --page-padding: 32px;
  --page-padding-tablet: 8px;
  --page-width: calc(866px + var(--page-padding) + var(--page-padding));
}

.full-screen {
  height: 100vh;
  position: relative;
}

.removed {
  display: none !important;
}

.position-fixed {
  position: fixed;
}

.overflowMargin {
  margin-left: calc(100vw - 100%);
}

.container {
  margin: 0 auto;
  max-width: var(--page-width);
  width: 100%;
  padding-left: var(--page-padding);
  padding-right: var(--page-padding);
}
.container.full-width {
  max-width: 100%;
}
.container.no-padding {
  padding-left: 0;
  padding-right: 0;
}
@media screen and (max-width: 920px) {
  .container {
    padding-left: var(--page-padding-tablet);
    padding-right: var(--page-padding-tablet);
  }
}
.container + .container-background {
  position: absolute;
  top: 0px;
  bottom: 0px;
  left: 0px;
  right: 0px;
  z-index: -1;
  max-width: 100%;
  width: 100%;
  display: flex;
}

.content-box {
  display: flex;
  flex-direction: column;
}

@media screen and (min-width: 920px) {
  .grid {
    display: grid;
    grid-auto-rows: var(--grid-row-size);
    row-gap: var(--row-gap);
    grid-row-gap: var(--row-gap);
    grid-template-columns: repeat(var(--columns), 1fr);
    column-gap: var(--column-gap);
    grid-column-gap: var(--column-gap);
  }
  .grid.stretch {
    grid-auto-rows: minmax(var(--grid-row-size), auto);
  }
  .grid > *:not([data-type=grid-ghost]):not([data-type=grid-pattern]) {
    overflow: hidden;
  }
  .grid > *[data-type=grid-spacer] {
    grid-column-start: 1;
    grid-column-end: 13;
  }
}
@media screen and (max-width: 920px) {
  .grid {
    display: flex;
    flex-direction: column;
  }
}

