.image-container {
  width: 100%;
  height: 100%;
}
.image-container img,
.image-container svg {
  width: 100%;
  height: 100%;
}
.image-container.cover img,
.image-container.cover svg {
  object-fit: cover;
}
.image-container.contain img,
.image-container.contain svg {
  object-fit: contain;
}
@media screen and (min-width: 920px) {
  .image-container.fit-grid {
    position: relative;
  }
  .image-container.fit-grid img {
    position: absolute;
    z-index: 1;
  }
  .image-container.fit-grid + * {
    z-index: 2;
  }
}
.image-container.img-align-start img,
.image-container.img-align-start svg {
  object-position: 50% 0;
}
.image-container.img-align-end img,
.image-container.img-align-end svg {
  object-position: 50% 100%;
}
.image-container.img-justify-start img,
.image-container.img-justify-start svg {
  object-position: 0 50%;
}
.image-container.img-justify-end img,
.image-container.img-justify-end svg {
  object-position: 100% 50%;
}

.overflow-hidden {
  overflow: hidden !important;
  background-color: rgba(0, 0, 0, 0.6);
