:root {
  --burger-border-radius: 0px;
  --burger-bar-height: 2px;
  --burger-bar-gap: 6px;
  --burger-bar-color: var(--secondary);
  --burger-box-width: 24px;
  --burger-box-height: 18px;
  --burger-transition-length: 0.4s;
  --burger-transition-delay: 0.1s;
  --burger-transition-timing-function: ease-in-out;
  --burger-container-animation-length: .25s;
  --burger-container-animation-delay: .25s;
  --burger-container-animation-timing-function: ease-in-out;
  --burger-container-animation-name-1: burger-fade-1;
  --burger-container-animation-name-2: burger-fade-2;
}

:root {
  --color-text-link-primary: var(--primary);
  --color-text-link-primary-hover: var(--primary-accent);
  --color-text-link-primary-down: var(--primary-dark);
  --color-text-link-primary-disabled: var(--primary-light);
  --link-transition-duration: 250ms;
  --link-transition-function: ease-in-out;
}

:root {
  --font-primary: "Heebo", sans-serif;
  --font-secondary: "Roboto", sans-serif;
  --heading-1-color: var(--light);
  --heading-1-size: 60px;
  --heading-1-line: 1.2;
  --heading-1-weight: 300;
  --heading-1-letter: normal;
  --heading-1-transform: capitalize;
  --heading-1-font: var(--font-primary);
  --heading-1-font-style: normal;
  --heading-2-color: var(--primary-dark);
  --heading-2-size: 11px;
  --heading-2-line: 1.82;
  --heading-2-weight: 500;
  --heading-2-letter: 0.34em;
  --heading-2-transform: uppercase;
  --heading-2-font: var(--font-primary);
  --heading-2-font-style: normal;
  --heading-3-size: 18px;
  --heading-3-line: 1.56;
  --heading-3-weight: normal;
  --heading-3-letter: 0.029em;
  --heading-3-transform: none;
  --heading-3-font: var(--font-primary);
  --heading-3-font-style: normal;
  --heading-3-color: var(--grey-200);
  --body-color: var(--light);
  --body-size: 16px;
  --body-line: 1.75;
  --body-weight: normal;
  --body-letter: normal;
  --body-transform: none;
  --body-font: var(--font-primary);
  --body-font-style: normal;
  --body-small-color: var(--primary-dark);
  --body-small-size: 12px;
  --body-small-line: 20px;
  --body-small-weight: normal;
  --body-small-letter: normal;
  --body-small-transform: none;
  --body-small-font: var(--font-primary);
  --body-small-font-style: normal;
  --button-size: 16px;
  --button-line: 1.6;
  --button-weight: bold;
  --button-letter: normal;
  --button-transform: none;
  --button-font: var(--font-primary);
  --button-font-style: normal;
  --tagline-size: 10px;
  --tagline-line: 2;
  --tagline-weight: 500;
  --tagline-letter: 0.55em;
  --tagline-transform: none;
  --tagline-font: var(--font-primary);
  --tagline-font-style: normal;
  --tagline-color: var(--grey-200);
}
@media screen and (max-width: 920px) {
  :root {
    --heading-1-size: 32px;
    --heading-1-line: 1.37;
  }
}
@media screen and (max-width: 920px) {
  :root {
    --heading-2-line: 1.6;
  }
}
@media screen and (max-width: 920px) {
  :root {
    --body-size: 14px;
    --body-line: 1.57;
  }
}

.site-container {
  width: 100vw;
}

.landing {
  background: linear-gradient(252.73deg, var(--primary-accent) -8.13%, var(--secondary-accent) 104.7%);
  position: relative;
  overflow: hidden;
}
.landing-bubble {
  position: absolute;
  border-radius: 50%;
  box-shadow: 0px 6px 25px rgba(0, 0, 0, 0.05);
}
.landing-bubble-1 {
  width: 933.18px;
  height: 936px;
  left: -280px;
  top: -300px;
  animation: bubble1Turning 40s linear infinite;
}
@media screen and (max-width: 920px) {
  .landing-bubble-1 {
    width: 343px;
    height: 343px;
    top: -200px;
    left: -200px;
    animation: bubble1TurningMobile 40s linear infinite;
  }
}
.landing-bubble-2 {
  width: 881px;
  height: 881px;
  top: 400px;
  right: 0;
  animation: bubble1Turning 40s linear infinite reverse;
}
@media screen and (max-width: 920px) {
  .landing-bubble-2 {
    width: 343px;
    height: 343px;
    top: 340px;
    right: -200px;
    animation: bubble1TurningMobile 40s linear infinite reverse;
