  margin-right: auto;
}
.landing-logo-link {
  z-index: 999;
  position: relative;
  margin-bottom: 100px;
  display: block;
  width: 200px;
}
@media screen and (max-width: 920px) {
  .landing-logo-link {
    margin-bottom: 72px;
    width: 86px;
    height: 40px;
  }
}
.landing-disclaimer {
  color: var(--accent-2-dark);
  margin-bottom: 36px;
  font-weight: bold;
}
.landing-title {
  margin-bottom: 36px;
  max-width: 738px;
}
@media screen and (max-width: 920px) {
  .landing-title {
    width: 82vw;
    margin-bottom: 32px;
  }
}
.landing-text {
  max-width: 582px;
  margin-bottom: 24px;
}
@media screen and (max-width: 920px) {
  .landing-text {
    width: 77vw;
    margin-bottom: 56px;
  }
}
.landing-round-container {
  width: 26px;
  height: 40px;
  border: 1px solid var(--light);
  box-sizing: border-box;
  border-radius: 37px;
  position: relative;
  margin-bottom: 80px;
}
@media screen and (max-width: 920px) {
  .landing-round-container {
    display: none;
  }
}
.landing-dot {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: var(--light);
  position: absolute;
  top: 10px;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: dotMovement 1.5s infinite;
}
.landing-spinner-container {
  display: none;
}
.landing-spinner-container.active {
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.landing-spinner-container.active .spinner {
  width: 148px;
  height: 148px;
  margin-bottom: 12px;
  opacity: 1;
  transition: opacity 1s;
}
@media screen and (max-width: 920px) {
  .landing-spinner-container.active .spinner {
    width: 100px;
    height: 100px;
  }
}
.landing-spinner-container.active .spinner.fading {
  opacity: 0;
}
.landing-spinner-container.active .circle {
  position: absolute;
  width: 148px;
  height: 148px;
  border-radius: 50%;
  border: 9px solid transparent;
}
@media screen and (max-width: 920px) {
  .landing-spinner-container.active .circle {
    width: 100px;
    height: 100px;
    border: 6px solid transparent;
  }
}
.landing-spinner-container.active .circle-1 {
  animation: expandCircles 2s linear infinite;
}
.landing-spinner-container.active .circle-2 {
  animation: expandCircles 2s linear 1s infinite;
}
.landing-waiting-error-container {
  display: none;
}
.landing-waiting-error-container.active {
  width: 100%;
  padding: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
.landing-waiting-error-container .waiting-error-img {
  height: 80px;
  width: 140px;
  margin-bottom: 50px;
}
.landing-waiting-error-container .waiting-error-text {
  margin-bottom: 40px;
}
.landing-waiting-error-container .waiting-error-button {
  display: flex;
  align-items: center;
}
.landing-waiting-error-container .refresh-icon {
  width: 20px;
  height: 20px;
  margin-right: 11px;
}
.landing-spinner-text {
  display: none;
}
.landing-spinner-text.active {
  display: block;
  position: absolute;
  bottom: 70px;
  left: 50%;
  transform: translateX(-50%);
}
@media screen and (max-width: 920px) {
  .landing-spinner-text.active {
    width: 90%;
    font-size: 13px;
  }
}

.landing-settings {
