.landing-input-textarea {
  width: 100%;
  padding: 16px 20px;
  min-height: 144px;
  color: var(--dark);
  border: 1px solid var(--grey-300);
  resize: none;
  overflow: hidden;
  margin-bottom: 16px;
  text-align: left;
  outline: none;
}
@media screen and (max-width: 920px) {
  .landing-input-textarea {
    padding: 12px;
  }
}
.landing-input-textarea::placeholder {
  line-height: 28px;
}
@media screen and (max-width: 920px) {
  .landing-input-textarea::-webkit-scrollbar {
    -webkit-appearance: none;
