  align-items: center;
  box-shadow: 0px 6px 25px rgba(0, 0, 0, 0.05);
  transition: transform 0.4s ease-in-out;
  transform: translateY(-100%);
}
@media screen and (max-width: 920px) {
  .fixed-header {
    padding: 14px 20px;
  }
}
.fixed-header.active {
  transform: translateY(0);
}
.fixed-header-logo {
  width: 140px;
}
@media screen and (max-width: 920px) {
  .fixed-header-logo {
    width: 86px;
  }
}
.fixed-header-button {
  box-shadow: none;
  transition: box-shadow 0.3s;
  margin-right: unset;
  font-weight: 500;
  font-size: 10px;
  line-height: 14px;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  padding: 15px 24px;
}
.fixed-header-button:hover {
  box-shadow: 0px 4px 20px var(--accent-1-dark);
}

.landing-settings-input.in-backdrop {
  position: fixed;
  top: 50%;
  transform: translateY(-50%);
  width: 866px;
}
@media screen and (max-width: 920px) {
  .landing-settings-input.in-backdrop {
    width: calc(100vw - 16px);
    left: 8px;
    top: 0;
    transform: unset;
    max-height: 100%;
    overflow: scroll;
  }
}
.landing-settings-input.in-backdrop .backdrop-cross-container {
  border-radius: 50%;
  background: var(--light);
  width: 32px;
  height: 32px;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  margin-left: auto;
  margin-bottom: 40px;
}
@media screen and (max-width: 920px) {
  .landing-settings-input.in-backdrop .backdrop-cross-container {
    margin-bottom: 16px;
  }
}
.landing-settings-input.in-backdrop .backdrop-cross {
  width: 10px;
  height: 10px;
  display: block;
}
.landing-settings-input .backdrop-cross-container {
  display: none;
}

@keyframes bubble1Turning {
  0% {
    transform: rotate(0deg) translateX(100px) rotate(0deg);
  }
  100% {
    transform: rotate(360deg) translateX(100px) rotate(-360deg);
  }
}
@keyframes bubble1TurningMobile {
  0% {
    transform: rotate(0deg) translateX(100px) rotate(0deg);
  }
  100% {
    transform: rotate(360deg) translateX(100px) rotate(-360deg);
  }
}
@keyframes bubble2Turning {
  0% {
    transform: rotate(0deg) translateX(150px) rotate(0deg);
  }
  100% {
    transform: rotate(360deg) translateX(150px) rotate(-360deg);
  }
}
@keyframes bubble2TurningMobile {
  0% {
    transform: rotate(0deg) translateX(150px) rotate(0deg);
  }
  100% {
    transform: rotate(360deg) translateX(150px) rotate(-360deg);
  }
}
@keyframes dotMovement {
  from {
    opacity: 0.8;
    top: 14px;
  }
  to {
    opacity: 0;
    top: calc(100% - 14px);
  }
}
@keyframes settingsSlide {
  from {
    height: 0px;
  }
  to {
    height: 227px;
  }
}
@keyframes rotate-loader {
  from {
    transform: rotate(0);
  }
  to {
    transform: rotate(360deg);
  }
}
@keyframes expandCircles {
  0% {
    transform: scale(0);
    border-color: var(--light);
  }
  30% {
    border-color: var(--light);
  }
  100% {
    transform: scale(1);
    border-color: transparent;
  }
}

