.image-container {
	width: 100%;
	height: 100%;

	img,
	svg {
		width: 100%;
		height: 100%;
	}

	&.cover {

		img,
		svg {
			object-fit: cover;
		}
	}

	&.contain {

		img,
		svg {
			object-fit: contain;
		}
	}

	// Don't use for now
	&.fit-grid {
		@media screen and (min-width: $width-tablet) {

			position: relative;

			img {
				position: absolute;
				z-index: 1;
			}

			+* {
				z-index: 2;
			}
		}
	}

	&.img-align {
		&-start {

			img,
			svg {
				object-position: 50% 0;
			}
		}

		&-end {

			img,
			svg {
				object-position: 50% 100%;
			}
		}
	}

	&.img-justify {
		&-start {

			img,
			svg {
				object-position: 0 50%;
			}
		}

		&-end {

			img,
			svg {
				object-position: 100% 50%;
			}
		}
	}
}

