	--tagline-transform: none;
	--tagline-font: var(--font-primary);
	--tagline-font-style: normal;
	--tagline-color: var(--grey-200);
}

sources.../scss/sections/landing-top.scss:
.site-container {
  width: 100vw;
}

.landing {
  background: linear-gradient(
    252.73deg,
    var(--primary-accent) -8.13%,
    var(--secondary-accent) 104.7%
  );
  position: relative;
  overflow: hidden;

  &-bubble {
    position: absolute;
    border-radius: 50%;
    box-shadow: 0px 6px 25px rgba(0, 0, 0, 0.05);
  }

  &-bubble-1 {
    width: 933.18px;
    height: 936px;
    left: -280px;
    top: -300px;
    animation: bubble1Turning 40s linear infinite;

    @media screen and (max-width: $width-tablet) {
      width: 343px;
      height: 343px;
      top: -200px;
      left: -200px;
      animation: bubble1TurningMobile 40s linear infinite;
    }
  }

  &-bubble-2 {
    width: 881px;
    height: 881px;
    top: 400px;
    right: 0;
    animation: bubble1Turning 40s linear infinite reverse;

    @media screen and (max-width: $width-tablet) {
      width: 343px;
      height: 343px;
      top: 340px;
      right: -200px;
      animation: bubble1TurningMobile 40s linear infinite reverse;
    }
  }

  &-container {
    padding-top: 80px;
    padding-bottom: 200px;

    @media screen and (max-width: $width-tablet) {
      padding-top: 56px;
      padding-bottom: 64px;
    }

    & > * {
      margin-left: auto;
      margin-right: auto;
    }
  }

  &-logo-link {
    z-index: 999;
    position: relative;
    margin-bottom: 100px;
    display: block;
    width: 200px;

    @media screen and (max-width: $width-tablet) {
      margin-bottom: 72px;
      width: 86px;
      height: 40px;
    }
  }

  &-disclaimer {
    color: var(--accent-2-dark);
    margin-bottom: 36px;
    font-weight: bold;
  }

  &-title {
    margin-bottom: 36px;
    max-width: 738px;

    @media screen and (max-width: $width-tablet) {
      width: 82vw;
      margin-bottom: 32px;
    }
  }

  &-text {
    max-width: 582px;
    margin-bottom: 24px;

    @media screen and (max-width: $width-tablet) {
      width: 77vw;
      margin-bottom: 56px;
    }
  }

  &-round-container {
    width: 26px;
    height: 40px;
    border: 1px solid var(--light);
    box-sizing: border-box;
    border-radius: 37px;
    position: relative;
    margin-bottom: 80px;

    @media screen and (max-width: $width-tablet) {
      display: none;
    }
  }

  &-dot {
    width: 6px;
    height: 6px;
    border-radius: 50%;
    background: var(--light);
    position: absolute;
    top: 10px;
    left: 50%;
    transform: translate(-50%, -50%);
    animation: dotMovement 1.5s infinite;
  }

  &-spinner-container {
    display: none;

    &.active {
      display: flex;
      flex-direction: column;
      align-items: center;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);

      .spinner {
        width: 148px;
        height: 148px;
        margin-bottom: 12px;
        opacity: 1;
        transition: opacity 1s;

        @media screen and (max-width: $width-tablet) {
          width: 100px;
          height: 100px;
        }

        &.fading {
          opacity: 0;
        }
      }

      .circle {
        position: absolute;
        width: 148px;
        height: 148px;
        border-radius: 50%;
        border: 9px solid transparent;

        @media screen and (max-width: $width-tablet) {
          width: 100px;
          height: 100px;
          border: 6px solid transparent;
        }
      }

      .circle-1 {
        animation: expandCircles 2s linear infinite;
      }

      .circle-2 {
        animation: expandCircles 2s linear 1s infinite;
      }
    }
  }

  &-waiting-error-container {
    display: none;

    &.active {
      width: 100%;
      padding: 0 20px;
      display: flex;
      flex-direction: column;
      align-items: center;
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -50%);
    }

    .waiting-error-img {
      height: 80px;
      width: 140px;
      margin-bottom: 50px;
    }

    .waiting-error-text {
      margin-bottom: 40px;
    }

    .waiting-error-button {
      display: flex;
      align-items: center;
    }

    .refresh-icon {
      width: 20px;
      height: 20px;
      margin-right: 11px;
    }
  }

  &-spinner-text {
    display: none;

    &.active {
      display: block;
      position: absolute;
      bottom: 70px;
      left: 50%;
      transform: translateX(-50%);

      @media screen and (max-width: $width-tablet) {
        width: 90%;
        font-size: 13px;
      }
    }
  }
}

sources.../scss/sections/landing-settings.scss:
.landing {
	&-settings {
		position: relative;
		background: var(--light);
		box-shadow: 0px 6px 25px rgba(0, 0, 0, 0.05);
		border-radius: 10px;
		padding: 40px 48px;
		z-index: 10;
		transition: height 0.3s;
		margin-bottom: 28px;

		@media screen and (max-width: $width-tablet) {
			padding: 0;
			height: 48px;
			margin-bottom: 10px;
		}

		&.active {
			height: 296px;
		}

		&-top {
			display: flex;
			justify-content: space-between;
			margin-bottom: 24px;

			@media screen and (max-width: $width-tablet) {
				margin-bottom: 0;
				padding: 16px 24px;
				transition: padding 0.3s;
			}

			&.active {
				padding: 28px 24px;
			}
		}

		&-toggling-container {
			position: relative;
			width: 10px;
			height: 10px;
			display: none;

			@media screen and (max-width: $width-tablet) {
				display: block;
			}

			span {
				position: absolute;
				display: block;
				border-radius: 1px;
				-webkit-border-radius: 1px;
				-moz-border-radius: 1px;
				background: var(--primary-dark);
				left: 50%;
			}

			.line-v {
				width: 1px;
				height: 10px;
				opacity: 1;
				transition: all 0.2s;

				&.remove-v {
					opacity: 0;
				}
			}

			.line-h {
				width: 1px;
				height: 10px;
				transform: rotate(-90deg);
			}
		}

		&-bottom {
			display: flex;
			justify-content: space-between;

			@media screen and (max-width: $width-tablet) {
				opacity: 0;
				transition: opacity 0.3s ease;
				height: 0;
			}

			&.active {
				opacity: 1;
			}
		}

		&-length-box,
		&-uniqueness-box,
		&-deepness-box {
			display: flex;
			flex-direction: row;
			flex-wrap: wrap;
			justify-content: space-between;
			width: 204px;

			@media screen and (max-width: $width-tablet) {
				width: 100%;
				float: left;
				padding: 0 24px;

				&:not(:last-of-type) {
					padding-bottom: 29px;
				}
			}
		}

		&-length-text,
		&-uniqueness-text,
		&-deepness-text {
			margin-bottom: 12px;
			font-size: 14px;

			@media screen and (max-width: $width-tablet) {
				margin-bottom: 0;
			}
		}

		&-length-number,
		&-uniqueness-number,
		&-deepness-number {
			font-size: 14px;
		}
	}
}

sources.../scss/sections/landing-input.scss:
.landing {
	textarea,
	input[type='text'] {
		-webkit-appearance: none;
		-moz-appearance: none;
		-o-appearance: none;
		appearance: none;
		box-shadow: none !important;
	}

	&-input {
		display: flex;
		flex-direction: column;
		position: relative;
		background: var(--light);
		box-shadow: 0px 6px 25px rgba(0, 0, 0, 0.05);
		border-radius: 10px;
		padding: 40px 48px;
		z-index: 10;
		transition: padding 0.5s, height 0.5s;

		@media screen and (max-width: $width-tablet) {
			padding: 28px 0 40px 0;
		}

		&-title {
			margin-bottom: 24px;

			@media screen and (max-width: $width-tablet) {
				margin-left: 24px;
			}
		}

		&-textarea-div {
			overflow: hidden;
			word-wrap: break-word;
			white-space: pre-wrap;
			width: 100%;
			height: 0px;
			border: none;
			-webkit-appearance: none;
			outline: none;
		}

		&-textarea-container {
			position: relative;

			@media screen and (max-width: $width-tablet) {
				margin: 0 12px;
			}
			.textarea-cross {
				display: none;
			}
			&.on-input {
				.landing-input-textarea {
					padding: 16px 58px 16px 20px;

					@media screen and (max-width: $width-tablet) {
						padding: 12px 38px 12px 12px;
					}
				}

				.textarea-cross {
					position: absolute;
					display: block;
					width: 18px;
					height: 18px;
					top: 21px;
					right: 20px;
					cursor: pointer;

					@media screen and (max-width: $width-tablet) {
						width: 14px;
						height: 14px;
						top: 18px;
						right: 12px;
					}
				}
			}
		}
		&-textarea {
			width: 100%;
			padding: 16px 20px;
			min-height: 144px;
			color: var(--dark);
			border: 1px solid var(--grey-300);
			resize: none;
			overflow: hidden;
			margin-bottom: 16px;
			text-align: left;
			outline: none;

			@media screen and (max-width: $width-tablet) {
				padding: 12px;
			}

			&::placeholder {
				line-height: 28px;
			}

			@media screen and (max-width: $width-tablet) {
				// Creating scroll bar similar to default one, as default one doesn't show in browsers in mobile. !important is needed sometimes //
				&::-webkit-scrollbar {
					-webkit-appearance: none;
				}

				&::-webkit-scrollbar:vertical {
					width: 7px !important;
					height: 100% !important;
				}

				&::-webkit-scrollbar-track:vertical {
					box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
					-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
					-webkit-border-radius: 10px !important;
					border-radius: 10px !important;
				}

				&::-webkit-scrollbar-track:horizontal {
					visibility: hidden;
				}

				&::-webkit-scrollbar-thumb {
					-webkit-border-radius: 10px !important;
					border-radius: 10px !important;
					background: rgba(0, 0, 0, 0.3) !important;
					box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
					-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3) !important;
				}
			}

			&.red-border {
				border-color: var(--accent-2-dark);
			}
		}

		&-count {
			color: var(--grey-200);
			margin-left: auto;
			margin-bottom: 6px;
			line-height: 18px;

			@media screen and (max-width: $width-tablet) {
				margin-right: 12px;
			}

			&.too-long {
				color: var(--accent-2);
			}
		}

		&-generate-text-button {
			box-shadow: none;
			transition: box-shadow 0.3s;
			width: max-content;
		}
	}
}

sources.../scss/sections/landing-sliders.scss:
.landing {
	&-settings {
		&-slider-container {
			width: 100%;
		}

		&-slider {
			-webkit-appearance: none;
			-moz-appearance: none;
			outline: none;
			width: 100%;
			height: 2px;
			border-radius: 2px;
			cursor: pointer;

			transition: background 450ms ease-in;
			-webkit-appearance: none;

			@media screen and (max-width: $width-tablet) {
				width: 100%;
				height: 4px;
			}
		}

		//Based on https://stackoverflow.com/a/57153340/12100458
		&-length-slider {
			background: linear-gradient(to right, var(--accent-1-accent) 0%, var(--accent-1-accent) 7.8%, var(--grey-300) 7.8%, var(--grey-300) 100%);
		}

		&-uniqueness-slider {
			background: linear-gradient(to right, var(--accent-1-accent) 0%, var(--accent-1-accent) 67%, var(--grey-300) 67%, var(--grey-300) 100%);
		}

		&-deepness-slider {
			background: linear-gradient(to right, var(--accent-1-accent) 0%, var(--accent-1-accent) 40%, var(--grey-300) 40%, var(--grey-300) 100%);
		}

		input[type='range']::-moz-focus-outer {
			border: 0;
		}

		&-slider::-webkit-slider-thumb {
			position: relative;
			-webkit-appearance: none;
			appearance: none;
			width: 10px;
			height: 10px;
			border-radius: 50%;
			background: var(--accent-1-accent);
			cursor: pointer;
			transition: all 0.2s;
			box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);

			@media screen and (max-width: $width-tablet) {
				width: 14px;
				height: 14px;
			}

			&:hover {
				transform: scale(1.2);
			}
		}

		&-slider::-moz-range-thumb {
			position: relative;
			-moz-appearance: none;
			appearance: none;
			width: 10px;
			height: 10px;
			border-radius: 50%;
			background: var(--accent-1-accent);
			cursor: pointer;
			transition: all 0.2s;
			box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.2);
			border: none;

			@media screen and (max-width: $width-tablet) {
				width: 14px;
				height: 14px;
			}

			&:hover {
				transform: scale(1.2);
			}
		}
	}
}

